CREATE TABLE "public"."course" ("id" serial NOT NULL, "title" text NOT NULL, "start_date" timestamptz NOT NULL, "end_date" timestamptz NOT NULL, "enrolment_start_date" timestamptz NOT NULL, "enrolment_end_date" timestamptz NOT NULL, "description" text NOT NULL, "created_at" timestamptz NOT NULL DEFAULT now(), "updated_at" timestamptz NOT NULL DEFAULT now(), "deleted_at" timestamptz, "created_by" text NOT NULL, "updated_by" text NOT NULL, "deleted_by" text NOT NULL, "badge_id" integer NOT NULL, PRIMARY KEY ("id") , FOREIGN KEY ("badge_id") REFERENCES "public"."badge"("id") ON UPDATE cascade ON DELETE cascade);
