CREATE TABLE "public"."enrolment" ("learner_id" integer NOT NULL, "course_id" integer NOT NULL, "trainer_id" integer NOT NULL, "name" text NOT NULL, "status_id" integer NOT NULL, PRIMARY KEY ("learner_id","course_id","trainer_id") , FOREIGN KEY ("status_id") REFERENCES "public"."status"("id") ON UPDATE cascade ON DELETE cascade, FOREIGN KEY ("learner_id") REFERENCES "public"."learner"("id") ON UPDATE cascade ON DELETE cascade, FOREIGN KEY ("course_id") REFERENCES "public"."course"("id") ON UPDATE cascade ON DELETE cascade, FOREIGN KEY ("trainer_id") REFERENCES "public"."trainer"("id") ON UPDATE cascade ON DELETE cascade);
